mapper.select:
select acco.a a, acco.mid a, aname,atel,aaddr,atype,acheckin, acheckout,aimg1,aimg2,aimg3,atheme, agrade , amaxprice , aminprice from ( select * from accomodation where atype= #{atype}
and asubway like '%'|| #{asubway} ||'%'
and agu like '%'|| #{agu} ||'%'
AND ROWNUM <= #{acurpage}*#{aonepage}
and atheme like '%'|| #{item} ||'%'
) acco left outer join (select a, min(rprice) aminprice, max(rprice) amaxprice from room group by a) ro on acco.a = ro.a left outer join (select NVL(floor(avg(vgrade)),1) agrade, a from review group by a) re on acco.a = re.a
order by writedate desc
order by aminprice asc
order by agrade desc

select a, atype,mid,aname, atel , aaddr, acheckin, acheckout, agil, amaxreservedate, amaxsleepdate, anotice, aimg1,aimg2,aimg3, atheme
from accomodation


	where mid = #{mid}


	where a = #{a}

mapper.update:
update accomodation
set writedate = sysdate

, aname = #{aname}


, atype = #{atype}


, aaddr = #{aaddr}


, atel = #{atel}


, amaxreservedate = #{amaxreservedate}


, amaxsleepdate = #{amaxsleepdate}


, acheckin = #{acheckin}


, acheckout = #{acheckout}


, aimg1 = #{aimg1}


, aimg2 = #{aimg2}


, aimg3 = #{aimg3}


, atheme  = #{atheme}


, anotice  = #{anotice}

where a = #{a}
