mapper.select:
select count(*) from review 


		where r = #{r}


		where mid = #{mid}

select v,r,re.u,vgrade,vimg1,vimg2,vimg3,vcontent,writedate, uuid from review re
,(select u,uuid from member) me
where re.u = me.u
 AND ROWNUM <= #{vcurpage}*#{vonepage} 


		and r=#{r}


order by r  desc

select v.vgrade, m.uuid, v.writedate, v.vcontent, v.vimg1
from review v join member m on m.u=v.u
where m.uuid=#{param1} and v.v=#{param2}
